/* ================= READING ================= */
// openStory is defined in index.html (inline script) so it runs in the same scope as showScreen/$

function toggleColorMode() {
  state.colorMode = !state.colorMode;
  renderUI();
}
function toggleAutoRead() {
  state.autoReading = !state.autoReading;
  renderUI();
  if (state.autoReading) autoReadNext();
}
function resetAutoRead() {
  state.autoReading = false;
  state.autoIndex = 0;
  document.querySelectorAll(".word").forEach((w) => w.classList.remove("playing", "highlighted"));
  renderUI();
}

function autoReadNext() {
  if (!state.autoReading) return;
  const words = [...document.querySelectorAll(".word")];
  if (state.autoIndex >= words.length) {
    resetAutoRead();
    return;
  }
  words.forEach((w) => w.classList.remove("playing"));
  const el = words[state.autoIndex];
  el.classList.add("playing");

  const raw = (el.dataset.word || el.textContent.trim() || "").toLowerCase();
  var w = null;
  if (el.dataset.soundFile) {
    w = { soundFile: el.dataset.soundFile, volume: el.dataset.volume != null ? parseFloat(el.dataset.volume) : 0 };
  } else {
    const wordsList = (typeof getExtendedWords === "function" && getExtendedWords()) ? getExtendedWords() : (typeof WORDS !== "undefined" ? WORDS : []);
    w = wordsList.length ? wordsList.find((x) => x && x.word && x.word.toLowerCase() === raw) : null;
  }

  var didAdvance = false;
  const advance = () => {
    if (didAdvance) return;
    didAdvance = true;
    state.audio.onended = null;
    state.audio.onerror = null;
    const text = (el.textContent || "").trim();
    const endsWithPeriod = /[.!?]$/.test(text);
    const endsWithComma = /,$/.test(text);
    const doAdvance = () => {
      state.autoIndex++;
      autoReadNext();
    };
    if (endsWithPeriod) {
      setTimeout(doAdvance, 850);
    } else if (endsWithComma) {
      setTimeout(doAdvance, 400);
    } else {
      doAdvance();
    }
  };

  if (w && w.soundFile) {
    state.audio.onended = advance;
    state.audio.onerror = advance;
    if (typeof applyAudioVolume === "function") applyAudioVolume(w.volume != null ? w.volume : 0);
    state.audio.src = w.soundFile;
    var p = state.audio.play();
    if (p && typeof p.catch === "function") p.catch(function() { if (!didAdvance) advance(); });
  } else {
    setTimeout(advance, 350);
  }
}

// Set of words that exist in WORDS (lowercase) for quick lookup
function getWordsSet() {
  if (typeof WORDS === "undefined") return new Set();
  var set = new Set();
  for (var i = 0; i < WORDS.length; i++) {
    if (WORDS[i] && WORDS[i].word) set.add((WORDS[i].word || "").toLowerCase());
  }
  return set;
}

function _escapeHtml(s) {
  if (s == null) return "";
  var d = document.createElement("div");
  d.textContent = s;
  return d.innerHTML;
}
// Converts **text** to <b>text</b> in a string; escapes HTML. Use for definitions and examples.
function _formatBold(s) {
  if (s == null) return "";
  var parts = String(s).split(/\*\*(.+?)\*\*/g);
  var out = "";
  for (var i = 0; i < parts.length; i++) {
    var escaped = _escapeHtml(parts[i]);
    if (i % 2 === 1) out += "<b>" + escaped + "</b>";
    else out += escaped;
  }
  return out;
}
function _formatEnExample(sentence, highlightWord, wordsSet) {
  if (!sentence || !sentence.trim()) return "";
  var parts = String(sentence).split(/\*\*(.+?)\*\*/g);
  var out = "";
  for (var i = 0; i < parts.length; i++) {
    if (i % 2 === 1) {
      out += "<b>" + _escapeHtml(parts[i]) + "</b>";
    } else {
      var tokens = (parts[i] || "").match(/\b[\p{L}']+\b|[^\p{L}']/gu) || [];
      for (var j = 0; j < tokens.length; j++) {
        var tok = tokens[j];
        if (/^[\p{L}']+$/u.test(tok)) {
          var lower = tok.toLowerCase();
          if (highlightWord && lower === (highlightWord || "").toLowerCase())
            out += "<b>" + _escapeHtml(tok) + "</b>";
          else if (wordsSet && !wordsSet.has(lower))
            out += "<span class=\"example-unknown\">" + _escapeHtml(tok) + "</span>";
          else
            out += _escapeHtml(tok);
        } else
          out += _escapeHtml(tok);
      }
    }
  }
  return out;
}
function _formatJpExample(sentence, highlightJp) {
  if (!sentence || !sentence.trim()) return _escapeHtml(sentence);
  var parts = String(sentence).split(/\*\*(.+?)\*\*/g);
  var out = "";
  for (var i = 0; i < parts.length; i++) {
    var seg = parts[i] || "";
    if (i % 2 === 1) {
      out += "<b>" + _escapeHtml(seg) + "</b>";
    } else if (highlightJp) {
      var escaped = _escapeHtml(seg);
      var escapedHighlight = _escapeHtml(highlightJp);
      out += escaped.split(escapedHighlight).join("<b>" + escapedHighlight + "</b>");
    } else {
      out += _escapeHtml(seg);
    }
  }
  return out;
}

// Build full word info HTML for the modal (definition + 3 example pairs). Used by story word click and dictionary.
function getWordInfoHtml(ww) {
  if (!ww) return "";
  var soundBtn = (ww.soundFile) ? "<button onclick=\"playSound('" + (ww.soundFile || "").replace(/'/g, "\\'") + "'," + (ww.volume != null ? ww.volume : 0) + ")\">ðŸ”Š</button>" : "";
  var phon = (state.phon === "IPA" ? ww.ipa : ww.ce) || "";
  var def = (state.lang === "EN" ? ww.enDef : ww.jpDef) || "";
  var wordsSet = typeof getWordsSet === "function" ? getWordsSet() : new Set();
  var en1 = ww.enEx1 != null ? ww.enEx1 : "";
  var jp1 = ww.jpEx1 != null ? ww.jpEx1 : "";
  var en2 = ww.enEx2 != null ? ww.enEx2 : "";
  var jp2 = ww.jpEx2 != null ? ww.jpEx2 : "";
  var en3 = ww.enEx3 != null ? ww.enEx3 : "";
  var jp3 = ww.jpEx3 != null ? ww.jpEx3 : "";
  var exHtml = "<p class=\"examples-title\"><strong>Examples:</strong></p>";
  exHtml += "<p class=\"example-en\">" + _formatEnExample(en1, ww.word, wordsSet) + "</p><p class=\"example-jp\">" + _formatJpExample(jp1, ww.jpWord) + "</p>";
  exHtml += "<p class=\"example-en\">" + _formatEnExample(en2, ww.word, wordsSet) + "</p><p class=\"example-jp\">" + _formatJpExample(jp2, ww.jpWord) + "</p>";
  exHtml += "<p class=\"example-en\">" + _formatEnExample(en3, ww.word, wordsSet) + "</p><p class=\"example-jp\">" + _formatJpExample(jp3, ww.jpWord) + "</p>";
  var posHtml = (ww.pos != null && ww.pos !== "") ? "<p class=\"word-pos\">" + _escapeHtml(ww.pos) + "</p>" : "";
  return "<h2>" + _escapeHtml(ww.word || "") + "</h2>" + soundBtn + posHtml + "<p>" + _escapeHtml(phon) + "</p><p>" + _escapeHtml(ww.jpWord || "") + "</p><p>" + _formatBold(def) + "</p>" + exHtml;
}

// CEFR order for "minimum" level: A1=0, A2=1, B1=2, B2=3, C1=4
var CEFR_ORDER = { A1: 0, A2: 1, B1: 2, B2: 3, C1: 4 };
function minCefr(a, b) {
  if (!a) return b;
  if (!b) return a;
  return (CEFR_ORDER[a] || 99) <= (CEFR_ORDER[b] || 99) ? a : b;
}

// Map of lowercase word -> minimum CEFR across all stories (so Full Dictionary can show every word with an entry)
var _storyWordsMapCache = null;
function getStoryWordsMap() {
  if (_storyWordsMapCache) return _storyWordsMapCache;
  var map = new Map();
  if (typeof STORIES === "undefined") return map;
  for (var s = 1; s < STORIES.length; s++) {
    var story = STORIES[s];
    if (!story.cefr || !story.lines || story.dynamic) continue;
    for (var l = 0; l < story.lines.length; l++) {
      var line = typeof story.lines[l] === "string" ? story.lines[l] : "";
      var tokens = line.match(/[\p{L}']+/gu) || [];
      // Merge 's tokens with the previous word token
      var mergedTokens = [];
      for (var ti = 0; ti < tokens.length; ti++) {
        var currentTok = tokens[ti];
        // If this token is "'s" (possessive) or just "s" after a word ending in apostrophe, merge with previous word
        if (mergedTokens.length > 0) {
          var prevTok = mergedTokens[mergedTokens.length - 1];
          // Check if previous token ends with apostrophe and current is "s", or current is "'s"
          if ((currentTok === "s" && /'$/.test(prevTok)) || currentTok === "'s") {
            // If previous token is a word (contains letters), merge 's with it
            if (/^[\p{L}']+$/u.test(prevTok)) {
              // Remove trailing apostrophe if present, then add 's
              mergedTokens[mergedTokens.length - 1] = prevTok.replace(/'$/, '') + "'s";
              continue;
            }
          }
        }
        mergedTokens.push(currentTok);
      }
      tokens = mergedTokens;
      for (var t = 0; t < tokens.length; t++) {
        var key = (tokens[t] || "").trim().toLowerCase();
        if (!key) continue;
        var prev = map.get(key);
        map.set(key, minCefr(prev, story.cefr));
      }
    }
  }
  _storyWordsMapCache = map;
  return map;
}

// WORDS plus minimal entries for every word that appears in stories but not in WORDS (so Full Dictionary is complete)
var _cachedExtendedWords = null;
function getExtendedWords() {
  if (typeof WORDS === "undefined") return [];
  if (_cachedExtendedWords) return _cachedExtendedWords;
  var set = getWordsSet();
  var list = WORDS.slice();
  var map = getStoryWordsMap();
  map.forEach(function(cefr, key) {
    if (!set.has(key)) {
      list.push({
        word: key,
        cefr: cefr,
        ipa: "",
        ce: "",
        sounds: 0,
        enDef: "\u2014",
        jpWord: "\u2014",
        jpDef: "\u2014",
        tallies: { hard: 0, skip: 0, easy: 0 }
      });
    }
  });
  _cachedExtendedWords = list;
  return list;
}

// Cache: soundFile path -> true (exists) or false (missing). Used when "Show only words needing audio" is on.
var _audioExistsCache = {};
var _audioCacheReady = false;
// Expose so index.html can style story words that have an entry but missing audio file.
function audioFileExists(path) { return path ? (_audioExistsCache[path] === true) : false; }
if (typeof window !== "undefined") window.audioFileExists = audioFileExists;
// Call before building "needing audio" list so new audio files are detected (e.g. after adding files).
function invalidateAudioExistsCache() {
  _audioExistsCache = {};
  _audioCacheReady = false;
}
if (typeof window !== "undefined") window.invalidateAudioExistsCache = invalidateAudioExistsCache;
var _audioCacheBatchSize = 12;
function buildAudioExistsCache(onReady) {
  if (_audioCacheReady) { if (onReady) onReady(); return; }
  var extended = typeof getExtendedWords === "function" ? getExtendedWords() : [];
  var paths = [];
  extended.forEach(function(w) {
    if (w && w.soundFile && paths.indexOf(w.soundFile) < 0) paths.push(w.soundFile);
  });
  if (!paths.length) { _audioCacheReady = true; if (onReady) onReady(); return; }
  var index = 0;
  function runNextBatch() {
    var batch = paths.slice(index, index + _audioCacheBatchSize);
    index += _audioCacheBatchSize;
    if (batch.length === 0) {
      _audioCacheReady = true;
      if (onReady) onReady();
      return;
    }
    var pending = batch.length;
    batch.forEach(function(path) {
      fetch(path, { method: "GET" }).then(function(r) {
        _audioExistsCache[path] = r.ok;
      }).catch(function() {
        _audioExistsCache[path] = false;
      }).finally(function() {
        pending--;
        if (pending === 0) runNextBatch();
      });
    });
  }
  runNextBatch();
}

// Returns words from extended list (WORDS + story-only words), filtered by reading CEFR, sorted Aâ€“Z. One line per starting letter, words joined by ", ".
// If state.fullDictOnlyMissingAudio is true, includes every word that needs audio: no soundFile OR soundFile points to missing file (uses _audioExistsCache). CEFR ignored; dedupes by word.
function getFullDictionaryLines() {
  var list = [];
  if (state.fullDictOnlyMissingAudio) {
    // Per-word: needsAudio = true if any entry has no soundFile or file missing. Then add story-only words.
    var wordInfo = Object.create(null);
    if (typeof WORDS !== "undefined" && Array.isArray(WORDS)) {
      for (var i = 0; i < WORDS.length; i++) {
        var w = WORDS[i];
        if (!w || !w.word) continue;
        var key = (w.word || "").toLowerCase();
        if (!key) continue;
        var hasWorkingAudio = w.soundFile && (_audioExistsCache[w.soundFile] === true);
        var needsAudio = !hasWorkingAudio;
        if (!wordInfo[key]) wordInfo[key] = { word: w.word, cefr: w.cefr, needsAudio: false };
        wordInfo[key].needsAudio = wordInfo[key].needsAudio || needsAudio;
      }
    }
    Object.keys(wordInfo).forEach(function(key) {
      if (wordInfo[key].needsAudio) list.push({ word: wordInfo[key].word, cefr: wordInfo[key].cefr });
    });
    var set = typeof getWordsSet === "function" ? getWordsSet() : new Set();
    var map = typeof getStoryWordsMap === "function" ? getStoryWordsMap() : new Map();
    map.forEach(function(cefr, key) {
      if (!key || wordInfo[key]) return;
      if (!set.has(key)) list.push({ word: key, cefr: cefr });
    });
  } else if (typeof getExtendedWords === "function") {
    var extended = getExtendedWords();
    list = extended.filter(function(w) { return w && w.word && state.readingCefrFilter.has(w.cefr); })
      .map(function(w) { return { word: w.word, cefr: w.cefr }; });
  }
  list.sort(function(a, b) { return (a.word || "").toLowerCase().localeCompare((b.word || "").toLowerCase()); });
  var byLetter = {};
  for (var i = 0; i < list.length; i++) {
    var w = list[i].word;
    var first = (w && w.charAt(0) || "").toUpperCase();
    if (!/^[A-Z]$/.test(first)) continue;
    if (!byLetter[first]) byLetter[first] = [];
    byLetter[first].push(w);
  }
  var lines = [];
  var letters = Object.keys(byLetter).sort();
  for (var j = 0; j < letters.length; j++) {
    lines.push(byLetter[letters[j]].join(", "));
  }
  return lines;
}

// Heteronym overrides: maps "storyIdx_lineIdx_wordIdx" to variant (e.g. "bow1", "read2").
// Run dumpHeteronymPositions() in the browser console to list all heteronym positions.
// Key = storyIdx_lineIdx_wordIdx (storyIdx 0=Full Dict, 1+=story; wordIdx = token index in line).
var HETERONYM_OVERRIDES = {
  "29_2_3": "read1", "30_4_6": "read1", "33_3_2": "read1", "94_2_4": "read1", "110_2_2": "read1", "127_2_6": "read1",
  "144_1_1": "read2", "156_1_5": "read2", "162_3_2": "read2", "202_2_2": "read2", "218_0_11": "read2", "220_1_17": "read2",
  "267_2_14": "read1", "300_1_16": "read1", "303_2_14": "read1", "311_0_22": "read2", "313_1_19": "read2",
  "401_1_19": "read1", "409_1_12": "read1", "431_0_3": "read2", "434_1_18": "wound1", "435_0_12": "read2",
  "436_1_14": "read2", "438_1_12": "read2", "448_0_18": "read2", "448_1_12": "wound2", "458_1_11": "read2",
  "431_0_3": "read2", "431_1_31": "read2", "431_8_22": "read2",
  "452_0_11": "bow1", "482_1_5": "bow2", "454_0_11": "bowed1", "456_0_7": "bowed2", "456_0_12": "bow2",
  "455_0_10": "content1", "455_2_2": "content1", "496_1_8": "content2"
};

function getHeteronymOverride(storyIdx, lineIdx, wordIdx) {
  var key = storyIdx + "_" + lineIdx + "_" + wordIdx;
  return HETERONYM_OVERRIDES[key] || null;
}

// Call from console to list all heteronym positions: (storyIdx, lineIdx, wordIdx, word)
// Use the output to fill in HETERONYM_OVERRIDES.
function dumpHeteronymPositions() {
  var heteronyms = ["bow", "bowed", "content", "wound", "read"];
  if (typeof STORIES === "undefined") return;
  for (var s = 1; s < STORIES.length; s++) {
    var story = STORIES[s];
    if (!story.lines || story.dynamic) continue;
    for (var l = 0; l < story.lines.length; l++) {
      var line = typeof story.lines[l] === "string" ? story.lines[l] : "";
      var tokens = line.match(/[\p{L}']+/gu) || [];
      var mergedTokens = [];
      for (var ti = 0; ti < tokens.length; ti++) {
        var t = tokens[ti];
        if (mergedTokens.length > 0 && ((t === "s" && /'$/.test(mergedTokens[mergedTokens.length - 1])) || t === "'s")) {
          if (/^[\p{L}']+$/u.test(mergedTokens[mergedTokens.length - 1]))
            mergedTokens[mergedTokens.length - 1] = mergedTokens[mergedTokens.length - 1].replace(/'$/, '') + "'s";
          continue;
        }
        mergedTokens.push(t);
      }
      for (var w = 0; w < mergedTokens.length; w++) {
        var raw = (mergedTokens[w] || "").toLowerCase();
        if (heteronyms.indexOf(raw) >= 0)
          console.log("storyIdx=" + s + " lineIdx=" + l + " wordIdx=" + w + " word=\"" + raw + "\" key=\"" + s + "_" + l + "_" + w + "\" story=\"" + (story.title || "") + "\"");
      }
    }
  }
}

// Stories: { title, lines[], cefr? } or { title, dynamic: "fullDictionary" }. Full Dictionary is built from WORDS when opened.
function getStoryCounts() {
  var counts = { A1: 0, A2: 0, B1: 0, B2: 0, C1: 0, C2: 0 };
  if (typeof STORIES === "undefined") return counts;
  for (var i = 1; i < STORIES.length; i++) {
    var cefr = STORIES[i].cefr;
    if (cefr && counts.hasOwnProperty(cefr)) counts[cefr]++;
  }
  return counts;
}
